import React from "react";
import Icon from "./Icon";
import "./SideBar.css";
import HomeIcon from "@mui/icons-material/Home";
import HomeOutlinedIcon from '@mui/icons-material/HomeOutlined';
import NotificationsNoneIcon from "@mui/icons-material/NotificationsNone";
import NotificationsIcon from '@mui/icons-material/Notifications';
import MailOutlineIcon from "@mui/icons-material/MailOutline";
import MailIcon from '@mui/icons-material/Mail';
import PermIdentityIcon from "@mui/icons-material/PermIdentity";
import SettingsOutlinedIcon from "@mui/icons-material/SettingsOutlined";
import BookmarkBorderOutlinedIcon from "@mui/icons-material/BookmarkBorderOutlined";
import TagIcon from '@mui/icons-material/Tag';
import TagOutlinedIcon from '@mui/icons-material/TagOutlined';
import { Link, useLocation } from 'react-router-dom';



const iconInfo = [
  {
    iconText: "Home",
    iconPicOutlined: HomeOutlinedIcon,
    iconPic: HomeIcon,
    link: "/",
  },
  {
    iconText: "Explore",
    iconPic: TagIcon,
    iconPicOutlined: TagOutlinedIcon,
    link: "/explore",
  },
  {
    iconText: "Notifications",
    iconPic:NotificationsIcon,
    iconPicOutlined: NotificationsNoneIcon ,
    link: "/notifications",
  },
  {
    iconText: "Messages",
    iconPic: MailIcon,
    iconPicOutlined: MailOutlineIcon,
    link: "/messages",
  },
  {
    iconText: "Bookmarks",
    iconPic: BookmarkBorderOutlinedIcon,
    iconPicOutlined: BookmarkBorderOutlinedIcon,
    link: "/bookmarks",
  },
  {
    iconText: "Profile",
    iconPic: PermIdentityIcon,
    iconPicOutlined: PermIdentityIcon,
    link: "/profile",
  },
  {
    iconText: "Settins",
    iconPic: SettingsOutlinedIcon,
    iconPicOutlined: SettingsOutlinedIcon,
    link: "/settings",
  },
];

function CreateIcons(icons) {
  let location = useLocation();
  if (location.pathname == icons.link) {
    return (
      <Icon
        active
        iconText={icons.iconText}
        IconPic={icons.iconPic}
        link={icons.link}
      />
    );
  }
  else { 
    return (
      <Icon
        iconText={icons.iconText}
        IconPic={icons.iconPicOutlined}
        link={icons.link}
      />
    );
  }
  
}

function SideBar() {
  return (
    <div className="sideBar">
      <h1 className="logo">Sirius</h1>
      {iconInfo.map(CreateIcons)}
    </div>
  );
}

export default SideBar;
